DEFINE TABLE script_migration SCHEMAFULL
    PERMISSIONS
        FOR select FULL
        FOR create, update, delete NONE;

DEFINE FIELD script_name ON script_migration TYPE string;
DEFINE FIELD executed_at ON script_migration TYPE datetime DEFAULT time::now();
DEFINE TABLE secrets SCHEMAFULL;

DEFINE FIELD user ON secrets TYPE record<users>;
DEFINE FIELD label ON secrets TYPE string;
DEFINE FIELD parts ON secrets TYPE int;
DEFINE FIELD threshold ON secrets TYPE int;
DEFINE FIELD status ON secrets TYPE string;
DEFINE FIELD created_at ON secrets TYPE datetime;

DEFINE TABLE shares SCHEMAFULL;

DEFINE FIELD secret ON shares TYPE record<secrets>;
DEFINE FIELD user ON shares TYPE record<users>;
DEFINE FIELD key ON shares TYPE int;
DEFINE FIELD share ON shares TYPE string;

DEFINE TABLE users SCHEMAFULL;

DEFINE FIELD username ON users TYPE string;
DEFINE FIELD public_key ON users TYPE string;
